INSERT INTO `objetivo_aprendizaje` (`ID_OBJETIVO`,`DESCRIPCION_OBJETIVO`,`ID_UNIDAD`) VALUES (6,'Expresar y crear trabajos de arte a partir de la observación del: \\n> entorno natural: _ gura humana y paisajes chilenos \\n> entorno cultural: personas y patrimonio cultural de Chile \\n> entorno artístico: obras de arte local, chileno, latinoamericano y del resto del mundo',2);
INSERT INTO `objetivo_aprendizaje` (`ID_OBJETIVO`,`DESCRIPCION_OBJETIVO`,`ID_UNIDAD`) VALUES (7,'Comunicar y explicar sus impresiones de lo que sienten y piensan de obras de arte por variados medios.',2);
INSERT INTO `objetivo_aprendizaje` (`ID_OBJETIVO`,`DESCRIPCION_OBJETIVO`,`ID_UNIDAD`) VALUES (8,'Escribir artículos informativos para comunicar información sobre un tema.',4);
INSERT INTO `objetivo_aprendizaje` (`ID_OBJETIVO`,`DESCRIPCION_OBJETIVO`,`ID_UNIDAD`) VALUES (11,'Demostrar comprensión de las narraciones leídas:\n› extrayendo información explícita e implícita\n› identificando y describiendo las características físicas y los sentimientos de los distintos personajes\n› recreando, por medio de distintas expresiones (dibujos, modelos tridimensionales u otras), el ambiente en el que ocurre la acción\n› estableciendo relaciones entre el texto y sus propias experiencias\n› emitiendo una opinión sobre un aspecto de la lectura',4);
INSERT INTO `objetivo_aprendizaje` (`ID_OBJETIVO`,`DESCRIPCION_OBJETIVO`,`ID_UNIDAD`) VALUES (12,'Expresarse de manera coherente y articulada sobre temas de su interés:\n› presentando información o narrando un evento relacionado con el tema\n› incorporando frases descriptivas que ilustren lo dicho\n› utilizando un vocabulario variado\n› pronunciando adecuadamente y usando un volumen audible\n› manteniendo una postura adecuada',4);
INSERT INTO `objetivo_aprendizaje` (`ID_OBJETIVO`,`DESCRIPCION_OBJETIVO`,`ID_UNIDAD`) VALUES (13,'Contar números naturales del 0 al 1000 de 2 en 2, de 5 en 5 y de 10 en 10 hacia adelante y hacia atrás, empezando por cualquier número menor que 1000.',5);
INSERT INTO `objetivo_aprendizaje` (`ID_OBJETIVO`,`DESCRIPCION_OBJETIVO`,`ID_UNIDAD`) VALUES (14,'Leer números naturales del 0 al 100 y representarlos en forma concreta, pictórica y simbólica.',5);
INSERT INTO `objetivo_aprendizaje` (`ID_OBJETIVO`,`DESCRIPCION_OBJETIVO`,`ID_UNIDAD`) VALUES (15,'Comparar y ordenar números naturales del 0 al 100 de menor a mayor y viceversa, usando material concreto y monedas nacionales de manera manual y/o por medio de software educativo.',5);
